if ($_GET['current_page']) {
  $newsPage = $_GET['current_page'];
} else {
  $newsPage = 1;
}

$itemOnPage = 5;


$allPages = array();

for ($i = 0; $i < round(count($newsList[$currentLang]) / 5, 0); $i++) {
  array_push($allPages, $i);  
}

$getPagination = showPagesButton($allPages, $newsPage);
$currentItem = ($newsPage - 1) * $itemOnPage;
$itemLength = verifyItemsLength($currentItem + 4, $newsList[$currentLang]);

function verifyItemsLength($number, $arrNews) {
  if ($number >= count($arrNews) - 1) {
    return count($arrNews) - 1;
  } else {
    return $number;
  }
}

function showPagesButton($pages, $currentPage) {
  $showPagesArray = array();

  if (count($pages) > 5) {  
    if ($currentPage < 4) {
      for ($i = 0; $i < 5; $i++) {
        array_push($showPagesArray, $pages[$i]);
      }
      array_push($showPagesArray, array(count($pages), '>'));

    } else if ($currentPage > 3 && $currentPage < count($pages) - 1) {

      array_push($showPagesArray, array(1, '<'));
      for ($i = $currentPage - 3; $i < $currentPage + 2; $i++) {
        array_push($showPagesArray, $pages[$i]);
      }

      if ($currentPage < count($pages) - 2) {
        array_push($showPagesArray, array(count($pages), '>'));
      }
    } else if ($currentPage <= count($pages)) {
      array_push($showPagesArray, array(1, '<'));

      for ($i = count($pages) - 5; $i <= count($pages) - 1; $i++) {
        array_push($showPagesArray, $pages[$i]);
      }
    }

  } else {
    for ($i = 0; $i < count($pages); $i++) {
      array_push($showPagesArray, $pages[$i]);
    }
  }
  

  return $showPagesArray;
}


for ($i = $currentItem; $i <= $itemLength; $i++) {
  $newsTile = $newsList[$currentLang][$i]['title'];
  $newsImg = $newsList[$currentLang][$i]['image'];
  $newsDesc = $newsList[$currentLang][$i]['description'];
  $newsBtn = $newsList[$currentLang][$i]['newsShowAllBtn'];

  if ($newsList[$currentLang][$i]['bgSize']) {
    $bgSize = $newsList[$currentLang][$i]['bgSize'];
  } else {
    $bgSize = '';
  };

  echo "<div class='news-item'>
          <div class='news-title'>$newsTile</div>
          <div class='news-description'>
            <div class='news-img' style='background-image: url(../images/news/$newsImg); $bgSize'></div>

            <div class='news-text'>$newsDesc</div>
          </div>

          <div class='show-hide-description'>$newsBtn</div>
        </div>";
};



echo('<div class="pagination">');
foreach ($getPagination as $value => $key) {
  if (is_array($key)) {
    echo("<a class='pagination-item pagination-arrow' href='./?page=news&lang=" . $currentLang . "&current_page=" . $key[0] . "'>" . $key[1] . "</a>");
  }
  
  if ($key === $newsPage - 1 && is_numeric($key)) {
    echo("<a class='pagination-item active' href='./?page=news&lang=" . $currentLang . "&current_page=" . ($key + 1) . "'>" . ($key + 1) . "</a>");
  } else if (is_numeric($key)) {
    echo("<a class='pagination-item' href='./?page=news&lang=" . $currentLang . "&current_page=" . ($key + 1) . "'>" . ($key + 1) . "</a>");
  }
}
